import React, {useEffect, useState} from 'react';
import {Pagination} from "react-bootstrap";
import {getAllDevices} from "../api/deviceApi";

const PaginationPubl = ({onClick,number}) => {
    const [total,setTotal] = useState(null)
    const [arrayFilter,setArrayFilter] = useState(0)
    useEffect(()=>{
        getAllDevices().then(resp=>setTotal(resp.count)).then(()=>setArrayFilter(Math.floor(total/number)))
    },[])
    let items = [];
    console.log(arrayFilter)
    for(let i = 1;i<arrayFilter;i++) {
        items.push(i)
    }
    console.log(items.length)
    return (
        items.map(item => {
            return <Pagination key={item} onClick={onClick}>{item}</Pagination>
        })
    );
};

export default PaginationPubl;
