import React, {useEffect, useState} from 'react';
import {Pagination} from "react-bootstrap";
import {getAllDevices} from "../api/deviceApi";

const PaginationPubl = ({onClick, number}) => {
    const [total, setTotal] = useState(null)
    useEffect(() => {
        getAllDevices().then(resp => setTotal(resp.count))
    }, [])
    let items = [];
    const pages = Math.floor(total / number)
    for (let i = 0; i <= pages; i++) {
        items.push(i)
    }


    return (
        <Pagination className={'mt-5'}>
            {items.map(item => {
                return <Pagination.Item key={item} onClick={()=>onClick(item+1)}>{item+1}</Pagination.Item>
            })}
        </Pagination>
    );
};

export default PaginationPubl;
