import React from 'react';
import {Pagination} from "react-bootstrap";


const PaginationPubl = ({onClick, number, total}) => {
    let items = [];
    const pages = Math.floor(total / number)
    console.log(pages)
    for (let i = 0; i <= pages; i++) {
        items.push(i)
    }
    let newItems = items.map(i => i + 1)


    return (
        <Pagination className={'mt-5'}>
            {newItems.map(item => {
                console.log(item)
                return <Pagination.Item key={item} onClick={() => onClick(item)}>{item}</Pagination.Item>
            })}
        </Pagination>
    );
};

export default PaginationPubl;
